import styles from "@/styles/components/page_header.module.css"
import Image from 'next/image';
import React, {useEffect, useRef} from "react";

type PageHeaderProps = {
    children: React.ReactNode;
    size?: "small" | "medium" | "large";
};

/**
 * The page header component. This component is used to display a header on a page, it will render a background image with a blurred div in the centre where any children will be rendered.
 *
 * @param {Object} props - Component props.
 * @param {React.ReactNode} props.children - The children to render in the page header.
 * @param {string} [props.size="medium"] - The size of the page header, can be "small", "medium" or "large".
 *
 * @returns {JSX.Element} The rendered page header component.
 */
export default function PageHeader({ children, size = "medium" }: PageHeaderProps) {

    // Get a constant reference to the page header
    const pageHeaderRef : React.RefObject<HTMLDivElement> = useRef(null);

    // Update the height of the page header
    useEffect(() => {
        const pageHeaderElement = pageHeaderRef.current;

        // Update CSS variable based on size
        if(pageHeaderElement)
            switch (size) {
                case "small":
                    pageHeaderElement.style.setProperty("--headerHeight", "36vh");
                    break;

                case "medium":
                    pageHeaderElement.style.setProperty("--headerHeight", "66vh");
                    break;

                case "large":
                    pageHeaderElement.style.setProperty("--headerHeight", "96vh");

            }
    }, [size]);

    return(
        <>
            {/* Page Header */}
            <div ref={pageHeaderRef} className={styles.pageHeader}>

                {/* Make the image container go behind the content*/}
                {/* Apply the styling to the div as <Image> uses fit="cover" to make image the size of the div */}
                <div className={"absolute -z-10 "+ styles.mainBG}>
                    {/* Background Image using Next.js Image Component, with blur placeholder for when the image is loading*/}
                    <Image
                        src={"/media/images/main_bg.jpg"}
                        alt={"Plants Background"}
                        fill={true}
                        objectFit="cover"
                        quality={100}
                        placeholder={"blur"}
                        blurDataURL={"data:image/jpeg;base64,"}
                    />
                </div>

                {/* This is the container for the header, it uses css to centre its contents*/}
                <div className={styles.headerContainer + " gridCentre"}>

                    {/* This is the blurred background for what ever is made to be displayed in the header */}
                    <div className={styles.contentContainer}>
                        <div>{children}</div>
                    </div>

                </div>
            </div>
        </>
    )
}